              "application/json": {
                "schema": {
                  "$ref": "#/components/schemas/Error"
                }
              }
            }
          }
        }
      }
    }
  },
  "components": {
    "schemas": {
      "SpacecraftId": {
        "description": "The unique identifier of a spacecraft",
        "type": "string"
      },
      "Spacecraft": {
        "type": "object",
        "required": [
          "id",
          "name",
          "type"
        ],
        "properties": {
          "id": {
            "$ref": "#/components/schemas/SpacecraftId"
          },
          "name": {
            "type": "string"
          },
          "type": {
            "type": "string",
            "enum": [
              "capsule",
              "probe",
              "satellite",
              "spaceplane",
              "station"
            ]
          },
          "description": {
            "type": "string"
          }
        }
      },
      "Error": {
        "type": "object",
        "required": [
          "message"
        ],
        "properties": {
          "message": {
            "description": "A human readable error message",
            "type": "string"
          }
        }
      }
    },
    "securitySchemes": {
      "ApiKey": {
        "type": "apiKey",
        "in": "header",
        "name": "X-Api-Key"
      }
    }
  },
  "security": [
    {
      "ApiKey": [

      ]
    }
  ]
}"
        }
    ]
}

{
    "type": "graphql",
    "files": [
        {
            "path": "posts.graphql",
            "content": "type Query {
  allPosts: [Post]
  allUsers: [User]
  postById(id: Int!): Post
  userById(id: Int!): User
}

type Mutation {
  createPost(input: CreatePostInput!): CreatePostPayload
  createUser(input: CreateUserInput!): CreateUserPayload
}

type Post {
  id: Int!
  title: String
  body: String
  createdDate: Datetime
  authorId: Int!
  userByAuthorId: User
}

type User {
  id: Int!
  username: String
  createdDate: Datetime
  postsByAuthorId: [Post]
}

type CreatePostPayload {
  clientMutationId: String
  post: Post
  userByAuthorId: User
}

input CreatePostInput {
  clientMutationId: String
  post: PostInput!
}

input PostInput {
  id: Int
  title: String
  body: String
  createdDate: Datetime
  authorId: Int!
}

type CreateUserPayload {
  clientMutationId: String
  user: User
}

input CreateUserInput {
  clientMutationId: String
  user: UserInput!
}

input UserInput {
  id: Int
  username: String
  createdDate: Datetime
}

scalar Datetime
"
        }
    ]
}

