{
    "content": "type Query {
  allPosts: [Post]
  allUsers: [User]
  postById(id: Int!): Post
  userById(id: Int!): User
}

type Mutation {
  createPost(input: CreatePostInput!): CreatePostPayload
  createUser(input: CreateUserInput!): CreateUserPayload
}

type Post {
  id: Int!
  title: String
  body: String
  createdDate: Datetime
  authorId: Int!
  userByAuthorId: User
}

type User {
  id: Int!
  username: String
  createdDate: Datetime
  postsByAuthorId: [Post]
}

type CreatePostPayload {
  clientMutationId: String
  post: Post
  userByAuthorId: User
}

input CreatePostInput {
  clientMutationId: String
  post: PostInput!
}

input PostInput {
  id: Int
  title: String
  body: String
  createdDate: Datetime
  authorId: Int!
}

type CreateUserPayload {
  clientMutationId: String
  user: User
}

input CreateUserInput {
  clientMutationId: String
  user: UserInput!
}

input UserInput {
  id: Int
  username: String
  createdDate: Datetime
}

scalar Datetime
"
}

