{
    "data": {
        "text": "/**
 * Function to query a Notion database.
 *
 * @param {Object} args - Arguments for the query.
 * @param {string} args.databaseId - The ID of the Notion database to query.
 * @param {Object} [args.filter] - The filter conditions for the query.
 * @param {Array} [args.sorts] - The sort conditions for the query.
 * @returns {Promise<Object>} - The result of the database query.
 */
const executeFunction = async ({ databaseId, filter, sorts }) => {
    // NOTE before using the tool: verify this section is correct
    const baseUrl = `https://api.notion.com/v1/databases/${databaseId}/query`;

    // NOTE! Replace this with your actual API token
    const token = process.env.NOTION_API_TOKEN;
    const notionVersion = process.env.NOTION_VERSION || '2022-06-28'; // Default version, update as needed

    // Set up headers for the request
    const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
        'Notion-Version': notionVersion,
    };

    const body = JSON.stringify({
        filter,
        sorts,
    });

    // Perform the fetch request
    const response = await fetch(baseUrl, {
        method: 'POST',
        headers,
        body
    });

    // Check if the response was successful
    if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData);
    }

    // Parse and return the response data
    const data = await response.json();
    return data;
};

/**
 * Tool configuration for querying a Notion database.
 * @type {Object}
 */
const apiTool = {
    type: 'function',
    function: {
        name: 'query_notion_database',
        function: executeFunction,
        description: 'Query a Notion database using specific filter and sort parameters.',
        parse: JSON.parse,
        parameters: {
            type: 'object',
            properties: {
                databaseId: {
                    type: 'string',
                    description: 'The ID of the Notion database to query.',
                },
                filter: {
                    type: 'object',
                    description: 'The filter conditions for the query.',
                },
                sorts: {
                    type: 'array',
                    description: 'The sort conditions for the query.',
                    items: {
                        type: 'object',
                        properties: {
                            property: {
                                type: 'string',
                                description: 'The database property to sort by.',
                            },
                            direction: {
                                type: 'string',
                                description: 'The direction of sorting, either "ascending" or "descending".',
                                enum: ['ascending', 'descending'],
                            },
                        },
                    },
                },
            },
            required: ['databaseId'],
            additionalProperties: false,
        },
    },
};

export default apiTool;"
    }
}

